// Normalisoi recommended_action vakiojoukkoon.
// Palauttaa 1→1: { ...ei.recommended_action = one of:
// 'none','read','review','follow_up','urgent','archive','calendar_consider' }

const MAP = [
  { keys: ['archive_after_review','archive'],                    out: 'archive' },
  { keys: ['review_and_decide','review_when_convenient','read_when_convenient','review_later','optional_review','review'], out: 'review' },
  { keys: ['follow_up_in_days','follow_up'],                     out: 'follow_up' },
  { keys: ['calendar_consider','consider_attendance'],           out: 'calendar_consider' },
  { keys: ['urgent','respond_now'],                              out: 'urgent' },
  { keys: ['read'],                                              out: 'read' },
];

function norm(x){ return (x ?? '').toString().trim().toLowerCase(); }
function mapAction(a){
  const s = norm(a);
  for (const m of MAP) if (m.keys.some(k => s.includes(k))) return m.out;
  return s || 'none';
}

return items.map(({ json }) => {
  if (!json?.ei?.message_id) throw new Error('normalize_ei: ei.message_id puuttuu');
  const ei = { ...json.ei };
  ei.recommended_action = mapAction(ei.recommended_action);
  // turvat: rajaa pisteet 0..100 jos tulevat yli/ali
  if (Number.isFinite(+ei.confidence))      ei.confidence      = Math.min(100, Math.max(0, +ei.confidence));
  if (Number.isFinite(+ei.priority_score))  ei.priority_score  = Math.min(100, Math.max(0, +ei.priority_score));
  return { json: { ...json, ei } };
});

// Yhtenäistää virhekirjauksen parametrit mark_errorille.
// Hakee message_id:n eri haaroista ja tiivistää virheviestin.

function pickMessageId(j){
  return j?.ca?.message_id
      || j?.ei?.message_id
      || j?.message_id
      || j?.id_for_join
      || j?.graph_id
      || j?.internet_message_id
      || null;
}

return items.map((item) => {
  const j = item.json || {};
  const mid = pickMessageId(j);
  if (!mid) throw new Error('prepare_error_params: message_id puuttuu virhehaarassa');

  // n8n error-branch: $json ja $error ovat käytettävissä
  const emsg = (item?.error?.message || item?.json?.error || 'unknown_error').toString();
  const eshort = emsg.length > 500 ? emsg.slice(0, 500) : emsg;

  return { json: { message_id: mid, err: eshort } };
});

// build_batch_prompt_cache — HYBRID VERSION

function sanitizeText(s) {
  if (s == null) return '';
  let t = String(s);
  t = t.replace(/\uFEFF/g, '');
  t = t.replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F]/g, '');
  return t.trim();
}

function pickMinimalInputFields(x) {
  return {
    message_id: x.message_id,
    subject: sanitizeText(x.subject),
    from: x.from_address || x.from_domain || '',
    text: sanitizeText(x.body_text || '')
  };
}

// Hae data edellisestä nodesta (merge_rules_to_batch)
const inputData = items[0].json;

// Debug
console.log('Input keys:', Object.keys(inputData));
console.log('batch_items exists?', !!inputData.batch_items);

// Poimi kentät
const batchItems = inputData.batch_items || [];
const batchSize = inputData.batch_size || batchItems.length;
const batchNumber = inputData.batch_number || 1;
const rulesVersion = inputData.rules_version || 'default_v1';
const rulesDsl = inputData.rules_dsl || '';
const rulesCount = inputData.rules_count || 0;

if (batchItems.length === 0) {
  console.error('ERROR: No batch_items to process!');
  return [{ json: { error: 'No batch_items' } }];
}

const message_count = batchItems.length;
const inputItems = batchItems.map(pickMinimalInputFields);
const batch_prompt = JSON.stringify(inputItems, null, 2);

// ========== SYSTEM PROMPT WITH RULES ==========
let system_prompt_base = `Olet rahoituskäyttöön koulutettu sähköpostiluokittelun asiantuntija.

RULES VERSION: ${rulesVersion}
RULES COUNT: ${rulesCount}`;

let rules_section = '';
if (rulesCount > 0 && rulesDsl) {
  rules_section = `

═══════════════════════════════════════════════
TUNNETUT LÄHETTÄJÄT JA SÄÄNNÖT (${rulesVersion})
═══════════════════════════════════════════════

Käytä näitä tunnettuja sääntöjä apuna luokittelussa:

${rulesDsl}

HUOM: Nämä säännöt perustuvat ${rulesCount} tunnettuun lähettäjään.
═══════════════════════════════════════════════
`;
}

const system_prompt_categories = `

PRIMARY_CATEGORY LUOKITTELU (PAKOLLINEN):

1. business_critical
2. personal_communication
3. financial_news
4. marketing
5. notifications
6. industry_news
7. internal
8. regulatory
9. spam_low_value
10. uncategorized

[... lisää kategoriamäärittelyt tähän ...]`;

const system_prompt = system_prompt_base + rules_section + system_prompt_categories;

const user_prompt = `Analysoi seuraavat ${message_count} sähköpostia ja palauta JSON-taulukko:\n\n${batch_prompt}`;

// Cache-muodossa
const system = [
  {
    type: "text",
    text: system_prompt,
    cache_control: { type: "ephemeral" }
  }
];

const messages = [
  {
    role: 'user',
    content: user_prompt
  }
];

return [{
  json: {
    batch_items: batchItems,
    batch_size: batchSize,
    batch_number: batchNumber,
    message_count,
    system,
    messages,
    rules_version: rulesVersion,
    rules_count: rulesCount,
    system_prompt,  // Debug
    user_prompt,    // Debug
    batch_prompt
  }
}];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

