// parse_ai_json — n8n Code (JavaScript)
// Parses AI response with robust error handling

// ========== HELPER FUNCTIONS ==========

function cleanInput(text) {
  return text
    .replace(/^\uFEFF/, '')
    .replace(/[\u200B-\u200D\uFEFF\u034F\u2060]/g, '')
    .replace(/```json\s*/gi, '')
    .replace(/```\s*/g, '')
    .replace(/…/g, '...')
    .trim();
}

function findLongestValidArray(text) {
  const candidates = [];

  for (let start = 0; start < text.length; start++) {
    if (text[start] !== '[') continue;

    for (let end = start + 1; end < text.length; end++) {
      if (text[end] !== ']') continue;

      const slice = text.slice(start, end + 1);
      try {
        const parsed = JSON.parse(slice);
        // KRIITTINEN: Hyväksy VAIN taulukot joissa on OBJEKTEJA
        if (Array.isArray(parsed) && 
            parsed.length > 0 && 
            typeof parsed[0] === 'object' && 
            parsed[0] !== null &&
            !Array.isArray(parsed[0])) {
          candidates.push({ parsed, length: parsed.length, size: slice.length });
        }
      } catch {}
    }
  }

  if (candidates.length === 0) return null;
  // Järjestä SIZE mukaan (pisin JSON), ei length mukaan
  candidates.sort((a, b) => b.size - a.size);
  return candidates[0].parsed;
}

function getAiText(json) {
  if (!json) return null;
  if (json.content && Array.isArray(json.content) && json.content[0]?.text) {
    return json.content[0].text;
  }
  if (typeof json === 'string') return json;
  if (json.text) return json.text;
  return null;
}

function createErrorPlaceholder(message_id, errorMsg) {
  return {
    message_id: message_id || 'unknown',
    _error: errorMsg,
    id: 0,
    classification: {
      category: 'uncategorized',
      primary_category: 'uncategorized',
      suggested_new_category: null,
      confidence: 0,
      is_known_pattern: false,
      suggested_rule: null
    },
    pattern_discovery: {
      domain_pattern: null,
      subject_pattern: null,
      content_indicators: []
    },
    priority_assessment: {
      priority: 0,
      requires_attention: false,
      action_needed: 'none',
      due_at_utc: null
    },
    entities: {
      companies: [],
      metrics: [],
      dates: []
    },
    recommendation: {
      category_action: 'use_existing',
      proposed_rule: null
    },
    content_analysis: {
      language: null,
      word_count: 0,
      sentence_count: 0,
      topics: [],
      ai_summary: errorMsg,
      ai_tags: [],
      sentiment_score: 0,
      business_relevance_score: 0,
      external_links: [],
      article_links: [],
      categories_found: [],
      content_structure: {
        content_location: 'mixed',
        has_substantial_text: false,
        primary_content_type: 'brief_summary',
        actionable_content: false
      },
      analyzer_version: 'claude-sonnet-4-5-20250929',
      analyzed_at_utc: new Date().toISOString()
    },
    decision: {
      model_name: 'claude',
      model_version: 'claude-sonnet-4-5-20250929',
      interest_label: 'uncategorized',
      interest_sublabel: null,
      confidence: 0,
      priority_score: 0,
      reasons: [errorMsg],
      rule_conditions: null,
      recommended_action: 'none',
      due_at_utc: null,
      status: 'proposed',
      decided_at_utc: new Date().toISOString()
    },
    analysis_level: 'signal'
  };
}

// ========== MAIN ==========

const aiOutput = $('ai_classify_emails_cache').first().json;
const batchData = $('build_batch_prompt_cache').first().json;  // ← KORJATTU!
const batchItems = batchData.batch_items || [];
const expected = batchData.message_count || batchData.batch_size || batchItems.length;

if (batchItems.length === 0) {
  return [{ json: createErrorPlaceholder(null, 'Ei viestejä käsiteltäväksi') }];
}

const rawIn = getAiText(aiOutput);

if (!rawIn) {
  return batchItems.map(item => ({
    json: createErrorPlaceholder(item.message_id, 'AI ei palauttanut tekstiä')
  }));
}

const cleaned = cleanInput(rawIn);
const arr = findLongestValidArray(cleaned);

if (!arr) {
  return batchItems.map(item => ({
    json: createErrorPlaceholder(item.message_id, 'JSON parsinta epäonnistui')
  }));
}

if (!Array.isArray(arr)) {
  return batchItems.map(item => ({
    json: createErrorPlaceholder(item.message_id, 'AI palautti ei-taulukon')
  }));
}

if (arr.length < expected) {
  const receivedIds = new Set(arr.map(x => x?.message_id).filter(Boolean));

  batchItems.forEach(item => {
    if (!receivedIds.has(item.message_id)) {
      arr.push(createErrorPlaceholder(item.message_id, 'AI ei palauttanut'));
    }
  });
}

return arr.map((obj, idx) => {
  if (!obj || typeof obj !== 'object') {
    const mid = batchItems[idx]?.message_id || `unknown_${idx}`;
    return { json: createErrorPlaceholder(mid, 'AI palautti virheellisen objektin') };
  }

  if (!obj.message_id) {
    obj.message_id = batchItems[idx]?.message_id || `unknown_${idx}`;
  }

  return { json: obj };
});

// build_log_ai — KORJATTU versio
return items.map(({ json }) => {
  if (!json.message_id) {
    throw new Error('build_log_ai: message_id puuttuu inputista');
  }

  if (!json.content_analysis) {
    throw new Error('build_log_ai: content_analysis puuttuu inputista');
  }

  if (!json.decision) {
    throw new Error('build_log_ai: decision puuttuu inputista');
  }

  const now = new Date().toISOString();  // ← LISÄTTY: yhteinen timestamp

  // Rakenna content_analysis (ca)
  const ca = {
    message_id: json.message_id,
    ...json.content_analysis,
    analyzed_at_utc: now,  // ← LISÄTTY!
    analysis_level: json.analysis_level || 'signal'
  };

  // Rakenna email_interest (ei) decision-osiosta
  const ei = {
    message_id: json.message_id,
    model_name: json.decision.model_name || 'claude',
    model_version: json.decision.model_version || 'claude-sonnet-4-5-20250929',
    interest_label: json.decision.interest_label || json.classification.primary_category,
    primary_category: json.classification.primary_category,
    interest_sublabel: json.decision.interest_sublabel || null,
    confidence: json.decision.confidence || json.classification.confidence || 0,
    priority_score: json.decision.priority_score || json.priority_assessment.priority || 0,
    reasons: json.decision.reasons || [],
    rule_conditions: json.decision.rule_conditions || json.classification.suggested_rule || null,
    recommended_action: json.decision.recommended_action || json.priority_assessment.action_needed || 'none',
    due_at_utc: json.decision.due_at_utc || json.priority_assessment.due_at_utc || null,
    status: json.decision.status || 'proposed',
    decided_at_utc: now,  // ← KORJATTU: käytä samaa timestampia
    requires_deep_analysis: json.decision.requires_deep_analysis || false
  };

  // Rakenna audit log
  const log = {
    message_id: json.message_id,
    analyzer_version: ca.analyzer_version || 'claude-sonnet-4-5-20250929',
    analyzed_at: now,  // ← KORJATTU: käytä samaa timestampia
    model_name: ei.model_name,
    model_version: ei.model_version,
    raw_analysis: json
  };

  return { json: { log, ca, ei } };
});

// Rakentaa DSL-tekstin classification_rules riveistä

const rules = items.map(i => i.json);

if (rules.length === 0) {
  return [{
    json: {
      version: 'default_v1',
      rules_json: [],
      rules_dsl: '',
      expires_at: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(),
      rules_count: 0,
      total_support: 0,
      avg_precision: 0
    }
  }];
}

// Järjestä prioriteetin ja tuen mukaan
rules.sort((a, b) => {
  if (a.priority !== b.priority) return a.priority - b.priority;
  return (b.support || 0) - (a.support || 0);
});

// Ryhmittele kategorioittain
const rulesByCategory = {};
rules.forEach(r => {
  if (!rulesByCategory[r.target_category]) {
    rulesByCategory[r.target_category] = [];
  }

  const precision = r.precision_cat_pct || 0;
  const confidence = precision >= 95 ? 'KORKEA' :
                     precision >= 80 ? 'HYVÄ' : 
                     precision >= 60 ? 'KOHTALAINEN' : 'MATALA';

  rulesByCategory[r.target_category].push({
    feature: r.feature,
    value: r.key_value,
    support: r.support || 0,
    precision: confidence,
    action: r.recommended_action || 'review'
  });
});

// Luo DSL-teksti
const dslLines = [];
for (const [category, categoryRules] of Object.entries(rulesByCategory)) {
  dslLines.push(`\n=== ${category.toUpperCase()} ===`);
  categoryRules.forEach(r => {
    const supportText = r.support > 0 ? `${r.support} osumaa` : 'ei dataa';
    dslLines.push(`- JOS ${r.feature}="${r.value}" → ${category} (${r.precision} tarkkuus, ${supportText}, toimenpide: ${r.action})`);
  });
}

const dsl = dslLines.join('\n');
const version = `hybrid_${new Date().toISOString().split('T')[0]}_r${rules.length}`;
const ttlHours = 24;
const expiresAt = new Date(Date.now() + ttlHours * 60 * 60 * 1000).toISOString();

const totalSupport = rules.reduce((sum, r) => sum + (r.support || 0), 0);
const avgPrecision = rules.length > 0 
  ? rules.reduce((sum, r) => sum + (r.precision_cat_pct || 0), 0) / rules.length
  : 0;

return [{
  json: {
    version: version,
    rules_json: rules,
    rules_dsl: dsl,
    expires_at: expiresAt,
    rules_count: rules.length,
    total_support: totalSupport,
    avg_precision: Math.round(avgPrecision * 100) / 100
  }
}];

// Yhdistää rules_snapshot datan batch_items dataan
// HUOM: Tämä node tarvitsee KAKSI inputtia!

// Input 0: Alkuperäinen batch data Start-nodesta
// Input 1: Rules data (cache hit TAI cache miss)

const inputs = $input.all();

// Debug: montako inputtia saadaan
if (!inputs || inputs.length === 0) {
  return [{
    json: {
      error: 'No inputs received',
      batch_items: [],
      rules_version: 'error_no_input'
    }
  }];
}

// Etsi batch data ja rules data
let batchData = null;
let rulesData = null;

// Käy läpi kaikki inputit
inputs.forEach(item => {
  const data = item.json;

  // Jos löytyy batch_items, se on batch data
  if (data.batch_items && Array.isArray(data.batch_items)) {
    batchData = data;
  }

  // Jos löytyy rules_version tai version, se on rules data  
  if (data.rules_version || data.version) {
    rulesData = data;
  }
});

// Jos batch data puuttuu, yritä hakea Start nodesta
if (!batchData) {
  try {
    const startNode = $('Start').first();
    if (startNode && startNode.json) {
      batchData = startNode.json;
    }
  } catch (e) {
    // Start node ei saatavilla
  }
}

// Fallback: jos ei batch dataa
if (!batchData || !batchData.batch_items) {
  batchData = {
    batch_items: [],
    batch_size: 0,
    batch_number: 1
  };
}

// Fallback: jos ei rules dataa
if (!rulesData) {
  rulesData = {
    version: 'default_v1',
    rules_json: [],
    rules_dsl: '',
    rules_count: 0
  };
}

// Yhdistä data
const merged = {
  // Batch kentät
  batch_items: batchData.batch_items || [],
  batch_size: batchData.batch_size || 0,
  batch_number: batchData.batch_number || 1,

  // Rules kentät
  rules_version: rulesData.version || rulesData.rules_version || 'default_v1',
  rules_dsl: rulesData.rules_dsl || '',
  rules_count: rulesData.rules_count || 0,
  rules_json: rulesData.rules_json || [],
  rules_from_cache: (rulesData.version && rulesData.version !== 'default_v1')
};

return [{ json: merged }];
